// Generated by Selenium IDE
require('geckodriver')
const { Builder, By, Key, until } = require('selenium-webdriver')
const firefox = require('selenium-webdriver/firefox');
const process = require('process');

describe('Add and delete MYOB transaction', function() {
  this.timeout(60000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('firefox')
      // .setFirefoxOptions(new firefox.Options().headless())
      .build()

      vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })

  async function login() {
    await driver.get("https://sit.login.myob.com/Account/Login?client_id=09f780c9-4873-4048-9fc5-f5943e91c817&redirect_uri=%2Foauth2%2Fauthorize%3Fresponse_type%3Dcode%2Bid_token%26client_id%3D09f780c9-4873-4048-9fc5-f5943e91c817%26resource%3D00000002-0000-0000-c000-000000000000%26scope%3Dopenid%26redirect_uri%3Dhttps%253a%252f%252ftest.secure.myob.com%252foauth2%252fAccount%252fLoginCallBack%26response_mode%3Dform_post%26state%")
    await driver.manage().window().setRect(1588, 944)

    // Login screen
    await driver.findElement(By.id("UserName")).sendKeys(process.env.SIT_EMAIL);
    await driver.findElement(By.id("Password")).sendKeys(process.env.SIT_PASSWORD);
    await driver.findElement(By.css(".btn-block")).click()
  }

  it('Add and delete MYOB transaction', async () => {
    await login();

    // Practice select
    let dropdown = await driver.wait(until.elementLocated(By.css(".form-control")));
    await dropdown.findElement(By.xpath("//option[. = 'AONZDB1']")).click(); 
    await driver.findElement(By.css("option:nth-child(2)")).click()
    await driver.findElement(By.css(".btn")).click()

    // Client select
    await driver.wait(until.elementLocated(By.linkText("Compliance"))).click();
    await driver.wait(until.elementLocated(By.linkText("Data reconciliation"))).click();
    await driver.wait(until.elementLocated(By.xpath("//div[starts-with(@class, 'table-data__cell') and @title='D2-Company-NZTM']"))).click();

    // Choosing the required period
    dropdown = await driver.wait(until.elementLocated(By.id("period")));
    await dropdown.findElement(By.xpath("//option[. = '1 Apr 2019 - 31 Mar 2020']")).click();

    /* Making sure that the unreconciled transactions card is displayed, otherwise we can't
    add transactions */
    await driver.wait(until.elementLocated(By.xpath("//div[contains(., 'Unreconciled transactions')]")));

    // Add transaction
    await driver.wait(until.elementLocated(By.css(".btn__content:nth-child(2)"))).click();
    
    // Delete transaction 
    await driver.wait(until.elementLocated(By.css(".table-data__row:nth-child(4) .checkbox label"))).click()
    await driver.wait(until.elementLocated(By.css(".btn-default:nth-child(3) .btn__content"))).click()
    await driver.wait(until.elementLocated(By.css(".btn-confirm-delete .btn__content"))).click()
  });

  it('Reconcile a transaction', async () => {
    await login();
    this.timeout(60000);
    // Practice select
    let dropdown = await driver.wait(until.elementLocated(By.css(".form-control")));
    await dropdown.findElement(By.xpath("//option[. = 'AONZDB1']")).click(); 
    await driver.findElement(By.css("option:nth-child(2)")).click()
    await driver.findElement(By.css(".btn")).click()

    // Client select
    await driver.wait(until.elementLocated(By.linkText("Compliance"))).click();
    await driver.wait(until.elementLocated(By.linkText("Data reconciliation"))).click();
    await driver.wait(until.elementLocated(By.xpath("//div[starts-with(@class, 'table-data__cell') and @title='D2-Company-NZTM']"))).click();

    // Choosing the required period
    dropdown = await driver.wait(until.elementLocated(By.id("period")));
    await dropdown.findElement(By.xpath("//option[. = '1 Apr 2019 - 31 Mar 2020']")).click();

    // Reconciling a transaction
    await driver.wait(until.elementLocated(By.css(".table-data__body .table-data__row:nth-child(1) .checkbox label"))).click()
    await driver.wait(until.elementLocated(By.css(".table-data__body .table-data__row:nth-child(2) .checkbox label"))).click()
    await driver.wait(until.elementLocated(By.xpath("//div[. ='Reconcile']"))).click();

    // Unreconciling a transaction
    await driver.wait(until.elementLocated(By.css(".groupedRows:nth-of-type(2) .checkbox label"))).click();
    await driver.wait(until.elementLocated(By.xpath("//div[. ='Reconcile']"))).click();
  
  });
})
